import React from "react";
import "./Installation.css";
import NodeIcon from '../icons/icon_node';
import { Grid, Paper, Button, Fab, Box, Container, Typography } from '@material-ui/core';
import { Dashboard as DashboardIcon } from '@material-ui/icons';
import { withStyles, makeStyles  } from '@material-ui/core/styles';
import { LinkContainer } from "react-router-bootstrap"
import TopMenuBar from "../components/TopMenuBar";
import BottomButtons from '../components/bottomButtons';
import { flexbox } from '@material-ui/system';

const nodes = [
  {
    id: '1FG5dh7h1',
    name: 'FakeNode 1',
    group: 'Tomatoes',
    status: 'Offline',
    x: 65,
    y: 45,
    z: 10
  },{
    id: '1FG5dh7h2',
    name: 'FakeNode 2',
    group: 'Carrots',
    status: 'Online',
    x: 45,
    y: 45,
    z: 10
  },{
    id: '1FG5dh7h3',
    name: 'FakeNode 3',
    group: 'Tomatoes',
    status: 'Online',
    x: 65,
    y: 55,
    z: 10
  },{
    id: '1FG5dh7h4',
    name: 'FakeNode 4',
    group: 'Carrots',
    status: 'Online',
    x: 45,
    y: 55,
    z: 10
  },{
    id: '1FG5dh7h5',
    name: 'FakeNode 5',
    group: 'Carrots',
    status: 'Offline',
    x: 45,
    y: 65,
    z: 10
  },{
    id: '1FG5dh7h6',
    name: 'FakeNode 6',
    group: 'Tomatoes',
    status: 'Online',
    x: 65,
    y: 65,
    z: 10
  },{
    id: '1FG5dh7h7',
    name: 'FakeNode 7',
    group: 'Tomatoes',
    status: 'Online',
    x: 65,
    y: 75,
    z: 10
  },
];

const useStyles = makeStyles(theme => ({
  root:{
    flexGrow: 1,
    minHeight: '100vh',
    minWidth: '100vw',
    display: 'flex',
    position: 'absolute',
    bottom: 0,
    flexWrap: 'wrap',
    alignItems: 'center',
    alignContent: 'stretch',
    justifyContent: 'center',
    flexDirection: 'column',
  },
  height: {
    height: '100%',
    width: '100%',
    margin: 0,
    padding: 0,
    zIndex: 0,
  },
  width:{
    maxWidth:'100%',
    width: '100%',
    marginLeft: theme.spacing(3),
    marginRight: theme.spacing(3),
  },
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'stretch'
  },
  stretchGrid: {
    height: '100%',
    width: '100%',
  },
  topButton:{
    minWidth: '100%',
    marginBottom: theme.spacing(2),
    marginTop: theme.spacing(10),
  },
  allButton:{
    marginBottom: theme.spacing(2),
    marginRight: theme.spacing(2),
    marginLeft: theme.spacing(2)
  },
  remMargin: {
    paddingLeft: 0,
    paddingRight: 0,
  }
}));

export default function Home() {
  const offlineNodes = nodes.filter((node) => node.status === 'Offline');
  const onlineNodes = nodes.filter((node) => node.status === 'Online');
  const classes = useStyles();

  return (
    <main>
    <header>
      <TopMenuBar
      block pageName="Home" hamburgerMenu={true} closeButtonOnly={false} closeWithPrompt={false} backButton={false} backRoutePage="/"/>
      </header>
      <body className={classes.height}>
        <div className={classes.root}>
          <Box display="flex"
            flexGrow={2}
            className={classes.topButton}
            >
              <Paper className={classes.width}>
              <LinkContainer to="/nodes">
                <Button component="span" className={classes.nodeCount}>
                      {onlineNodes.length}/{nodes.length}
                      Online nodes
                </Button>
              </LinkContainer>
              <LinkContainer to="/nodes">
                <Button component="span" className={classes.nodeCount}>
                      {offlineNodes.length}/{nodes.length}
                      Offline nodes
                </Button>
              </LinkContainer>
              </Paper>

            </Box>
            <Box
              display="flex"
              flexGrow={1}
              style={{width: '100vw',
                    maxWidth: '100%',
                    }}
              >
                <Grid container>
                  <Grid item container xs={12} spacing={2} className={classes.allButton}>
                    <Grid item xs={6} md={12}>
                    <Button variant="contained"color="secondary" className={classes.stretchGrid}>
                      <Typography variant="h6">Dashboard</Typography>
                    </Button>
                    </Grid>
                    <Grid item xs={6} md={12}>
                    <Button variant="contained" size="large" color="secondary" className={classes.stretchGrid}>
                      <Typography variant="h6">Add node</Typography>
                    </Button>
                    </Grid>
                  </Grid>
                </Grid>
              </Box>
          </div>
      </body>
      </main>
  );
}
