import React, {Component} from "react";
import "../containers/Home.css";
import {
  Button,
  Container,
  Typography,
  Grid,
  Box,
  LinearProgress,
  makeStyles,
} from '@material-ui/core';
import { LinkContainer } from "react-router-bootstrap"
import TopMenuBar from "../components/TopMenuBar";
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';

const useStyles = makeStyles(theme => ({
  avatarColor:{
    backgroundColor:'#fff',
  },
}))


export function MapContainer() {
    return (
        <Container>
          <TopMenuBar block pageName="Installation" hamburgerMenu={false} closeButtonOnly={false} closeWithPrompt={true} backButton={false} backRoutePage="/"/>
          <Grid container direction="column" alignItems="center" justify= "center" spacing={2}>
            <Grid item>
              <Typography variant="h4" align="center">Nodes</Typography>
              <Typography display="block" variant="body1">
                Position the nodes on the map
              </Typography>
        <Map google={this.props.google} zoom={14}>

          <Marker onClick={this.onMarkerClick}
                  name={'Current location'} />

          <InfoWindow onClose={this.onInfoWindowClose}>
            <div>
              <h1>{this.state.selectedPlace.name}</h1>
            </div>
          </InfoWindow>
        </Map>
            </Grid>
          </Grid>
        </Container>
    );
}

export default GoogleApiWrapper({
  apiKey: ("AIzaSyAzSWKgTvVRpKIPrzPCdIAjxhRhrZW-AsE")
})(MapContainer)
