import React from "react";
import "./Installation.css";
import NodeIcon from '../icons/icon_node';
import { Grid, Paper, Button, Fab, Box, Container } from '@material-ui/core';
import { Dashboard as DashboardIcon } from '@material-ui/icons';
import { withStyles, makeStyles  } from '@material-ui/core/styles';
import { LinkContainer } from "react-router-bootstrap"
import TopMenuBar from "../components/TopMenuBar";
import BottomButtons from '../components/bottomButtons';
import { flexbox } from '@material-ui/system';

const nodes = [
  {
    id: '1FG5dh7h1',
    name: 'FakeNode 1',
    group: 'Tomatoes',
    status: 'Offline',
    x: 65,
    y: 45,
    z: 10
  },{
    id: '1FG5dh7h2',
    name: 'FakeNode 2',
    group: 'Carrots',
    status: 'Online',
    x: 45,
    y: 45,
    z: 10
  },{
    id: '1FG5dh7h3',
    name: 'FakeNode 3',
    group: 'Tomatoes',
    status: 'Online',
    x: 65,
    y: 55,
    z: 10
  },{
    id: '1FG5dh7h4',
    name: 'FakeNode 4',
    group: 'Carrots',
    status: 'Online',
    x: 45,
    y: 55,
    z: 10
  },{
    id: '1FG5dh7h5',
    name: 'FakeNode 5',
    group: 'Carrots',
    status: 'Offline',
    x: 45,
    y: 65,
    z: 10
  },{
    id: '1FG5dh7h6',
    name: 'FakeNode 6',
    group: 'Tomatoes',
    status: 'Online',
    x: 65,
    y: 65,
    z: 10
  },{
    id: '1FG5dh7h7',
    name: 'FakeNode 7',
    group: 'Tomatoes',
    status: 'Online',
    x: 65,
    y: 75,
    z: 10
  },
];

const useStyles = makeStyles(theme => ({
  root:{
    flexGrow: 1,
    minHeight: '100vh',
    display: 'flex',
    position: 'absolute',
    paddingTop: '48px',
    bottom: 0,
    alignItems: 'center',
    justifyContent: 'center',
    flexDirection: 'column'
  },
  height: {
    height: '100%',
    margin: 0,
    padding: 0,
  },
  width:{
    width: '100%'
  },
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'stretch'
  }
}));

export default function Home() {
  const offlineNodes = nodes.filter((node) => node.status === 'Offline');
  const onlineNodes = nodes.filter((node) => node.status === 'Online');
  const classes = useStyles();

  return (
    <body className={classes.height}>
      <TopMenuBar style={{zIndex: 1}} block pageName="Home" hamburgerMenu={true} closeButtonOnly={false} closeWithPrompt={false} backButton={false} backRoutePage="/"/>
      <Container>
        <div className={classes.root}>
          <Box display="flex"
            flexGrow={1}>
              <Paper>
              <LinkContainer to="/nodes">
                <Button component="span" className={classes.nodeCount}>
                      {onlineNodes.length}/{nodes.length}
                      Online nodes
                </Button>
              </LinkContainer>
              <LinkContainer to="/nodes">
                <Button component="span" className={classes.nodeCount}>
                      {offlineNodes.length}/{nodes.length}
                      Offline nodes
                </Button>
              </LinkContainer>
              </Paper>

            </Box>
            <Box
              display="flex"
              flexGrow={1}
              >
                <Grid container>
                  <Paper className={classes.width}>
                    AARSLUL
                  </Paper>
                  <Paper className={classes.width}>
                    AARSLUL
                  </Paper>
                </Grid>
              </Box>
          </div>
          </Container>
      </body>
  );
}
