import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {
  Box,
  Container,
  Divider,
  Typography,
  Card,
  CardHeader,
  CardContent,
  CardActions,
  CardActionArea,
  Avatar,
  Button,
} from '@material-ui/core';
import ExpansionCard from '../components/ExpansionPanel.js';
import TopMenuBar from '../components/TopMenuBar.js'
import {LinkContainer} from 'react-router-bootstrap';
import NodeIcon from "../icons/icon_node.js";
import RouterIcon from '@material-ui/icons/Router';
import PersonIcon from '@material-ui/icons/Person';





const useStyles = makeStyles(theme => ({
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
  },
  section1: {
    margin: theme.spacing(2),
  },
  avatarColor:{
    backgroundColor:'#fff',
  },
}));

export default function SimpleExpansionPanel() {
  const classes = useStyles();

  return (
    <Container fixed>
      <TopMenuBar block pageName="FAQ" hamburgerMenu={false} closeButtonOnly={false} closeWithPrompt={false} backButton={true} backRoutePage="/"/>
      <Card className={classes.section1}>
      <LinkContainer to="/faq/node">
        <CardActionArea>
        <CardHeader
          avatar={
            <Avatar className={classes.avatarColor} aria-label="nodes">
              <NodeIcon color="primary" />
            </Avatar>
          }
          title={
            <Typography variant="h6">Node Troubleshoot</Typography>
          }
          />
          </CardActionArea>
          </LinkContainer>
      </Card>
      <Card className={classes.section1}>
      <LinkContainer to="/faq/node">
      <CardActionArea>
        <CardHeader
          avatar={
            <Avatar className={classes.avatarColor} aria-label="nodes">
              <RouterIcon color="primary" />
            </Avatar>
          }
          title="Gateway troubleshooting"
          />
          </CardActionArea>
          </LinkContainer>
      </Card>
      <LinkContainer to="/faq/node">
      <Card className={classes.section1}>
      <CardActionArea>
        <CardHeader
          avatar={
            <Avatar className={classes.avatarColor} aria-label="nodes">
              <PersonIcon color="primary" />
            </Avatar>
          }
          title="Profile troubleshooting"
          />
          </CardActionArea>
      </Card>
      </LinkContainer>
    </Container>
  );
}
