import React from "react";
import NodeIcon from '../icons/icon_node';
import { Grid, Paper, Button, Fab, Box, Container } from '@material-ui/core';
import { Dashboard as DashboardIcon } from '@material-ui/icons';
import { withStyles, makeStyles  } from '@material-ui/core/styles';
import { LinkContainer } from "react-router-bootstrap"
import TopMenuBar from "../components/TopMenuBar";

const nodes = [
  {
    id: '1FG5dh7h1',
    name: 'FakeNode 1',
    group: 'Tomatoes',
    status: 'Offline',
    x: 65,
    y: 45,
    z: 10
  },{
    id: '1FG5dh7h2',
    name: 'FakeNode 2',
    group: 'Carrots',
    status: 'Online',
    x: 45,
    y: 45,
    z: 10
  },{
    id: '1FG5dh7h3',
    name: 'FakeNode 3',
    group: 'Tomatoes',
    status: 'Online',
    x: 65,
    y: 55,
    z: 10
  },{
    id: '1FG5dh7h4',
    name: 'FakeNode 4',
    group: 'Carrots',
    status: 'Online',
    x: 45,
    y: 55,
    z: 10
  },{
    id: '1FG5dh7h5',
    name: 'FakeNode 5',
    group: 'Carrots',
    status: 'Offline',
    x: 45,
    y: 65,
    z: 10
  },{
    id: '1FG5dh7h6',
    name: 'FakeNode 6',
    group: 'Tomatoes',
    status: 'Online',
    x: 65,
    y: 65,
    z: 10
  },{
    id: '1FG5dh7h7',
    name: 'FakeNode 7',
    group: 'Tomatoes',
    status: 'Online',
    x: 65,
    y: 75,
    z: 10
  },
];

const useStyles = makeStyles(theme => ({
  mainGrid:{
    height: '100vmax',
  },
  gridItem: {
    width: '100%',
  },
  bottom: {
    position: 'absolute',
    bottom: "24px",
  },
  button:{
    width: '100%'
  }
}));

export default function Home() {
  const offlineNodes = nodes.filter((node) => node.status === 'Offline');
  const onlineNodes = nodes.filter((node) => node.status === 'Online');
  const classes = useStyles();

  return (
    <Container>
      <TopMenuBar block pageName="Home" hamburgerMenu={true} closeButtonOnly={false} closeWithPrompt={false} backButton={false} backRoutePage="/"/>
        <Grid container justify="center" alignItems="center">
          <Grid item xs={12}>
            <Paper>
            <LinkContainer to="/nodes">
              <Button component="span" className={classes.nodeCount}>
                    {onlineNodes.length}/{nodes.length}
                    Online nodes
              </Button>
            </LinkContainer>
            <LinkContainer to="/nodes">
              <Button component="span" className={classes.nodeCount}>
                    {offlineNodes.length}/{nodes.length}
                    Offline nodes
              </Button>
            </LinkContainer>
            </Paper>
          </Grid>

            <Grid item xs={12} className={classes.bottom}>
              <Grid container spacing={2}>
              <Grid item xs={6}>
                <LinkContainer to="/dashboard">
                  <Button variant="contained" color="primary" className={classes.button}>
                        <DashboardIcon className="icon"/>
                        Dashboard
                  </Button>
                </LinkContainer>
              </Grid>
              <Grid item xs={6} className={classes.button}>
                <LinkContainer to="/installation">
                  <Button variant="contained" color="primary">
                    <NodeIcon color="white"/>
                    Add nodes
                  </Button>
                </LinkContainer>
              </Grid>
              </Grid>
            </Grid>
          </Grid>
      </Container>
  );
}
