import React from "react";
import clsx from 'clsx';
import "../containers/Home.css";
import {
  Container,
  Typography,
  Box,
  Button,
  makeStyles,
  List,
  ListItem,
  CircularProgress,
  ListItemAvatar,
  Avatar,
  ListItemText,
  Zoom,
  Snackbar,
  SnackbarContent,
  Fade
} from '@material-ui/core';
import {green} from '@material-ui/core/colors';

const useStyles = makeStyles(theme => ({
  section1: {
    margin: theme.spacing(2),
  },
  avatarColor:{
    backgroundColor: "#599AD5"
  }
}));

export default function Step5(){
  const classes = useStyles()
  const [loading, setLoading] = React.useState(false);
  const [success, setSuccess] = React.useState(false);
  const timer = React.useRef();
  const [open, setOpen] = React.useState(false);

  const handleClose = () => {
    setOpen(false);
  };


  const buttonClassname = clsx({
    [classes.buttonSuccess]: success,
  });

  React.useEffect(() => {
    return () => {
      clearTimeout(timer.current);
    }
  }, []);

  const handleButtonClick = () => {
    if(!loading){
      setSuccess(false);
      setLoading(true);
      timer.current = setTimeout(() =>{
        setSuccess(true);
        setLoading(false);
        setOpen(true);
      }, 2000);
    }
  };

  return(
    <Container>
        <Fade in="true">
          <Box m={1}>
            <Typography variant="overline">
            Installation &#10140; Gateway
            </Typography>
            <Typography variant="h3">Gateway</Typography>
            <Typography display="block" variant="body1">
              Connecting network
            </Typography>
          </Box>
        </Fade>
        <Box m={1}>
          <List>
            <Zoom in={true} style={{ transitionDelay:'200ms' }}>
              <ListItem>
                <ListItemAvatar>
                  <Avatar className={classes.avatarColor}>
                    1
                  </Avatar>
                </ListItemAvatar>
                <ListItemText primary="Pick up the LAN cable from the provided box" />
              </ListItem>
            </Zoom>
            <Zoom in={true} style={{ transitionDelay:'500ms' }}>
              <ListItem>
                <ListItemAvatar color="primary">
                  <Avatar className={classes.avatarColor}>
                    2
                  </Avatar>
                </ListItemAvatar>
                <ListItemText primary="Plug one end of the LAN cable in your local router" />
              </ListItem>
            </Zoom>
            <Zoom in={true} style={{ transitionDelay:'800ms' }}>
              <ListItem>
                <ListItemAvatar>
                  <Avatar className={classes.avatarColor}>
                    3
                  </Avatar>
                </ListItemAvatar>
                <ListItemText primary="Plug the other end in the provided gateway" />
              </ListItem>
            </Zoom>
          </List>
        </Box>
    </Container>
  );
}
