import React from "react";
import "../containers/Home.css";
import {
  Button,
  Typography,
  Grid,
  Box,
  LinearProgress,
  makeStyles,
} from '@material-ui/core';
import { LinkContainer } from "react-router-bootstrap";
import PowerIcon from '@material-ui/icons/Power';
import RouterIcon from '@material-ui/icons/Router';
import SettingsEthernetIcon from '@material-ui/icons/SettingsEthernet';

const useStyles = makeStyles(theme => ({
  button: {
    bottom: 20,
    position: 'fixed',
    margin: 0,
  },
  iconAlign:{
    display: 'inline-flex',
    verticalAlign:'middle',
  }
}))

export default function Step2(){
  const classes = useStyles()
  return(
      <Grid container alignItems="center" justify="center" direction="column" spacing={2}>
        <Grid item sm={12}>
          <Box width={300} align="center">
            <Typography variant="overline">Progress</Typography>
            <LinearProgress variant="determinate" value={66}/>
          </Box>
        </Grid>
        <Grid item xs={12} >
          <Typography variant="h4" align="center">Open the box</Typography>
          <Typography display="block" variant="body1">
          In the box are the following items:
          </Typography>
        <Grid item xs={12}>
            <Typography className={classes.iconAlign} variant="h6"><RouterIcon /> Gateway</Typography>
        </Grid>
        <Grid item xs={8}>
            <RouterIcon/>
        </Grid>
          <Typography variant="h6">Nodes</Typography>
          <Typography variant="h6">LAN cable</Typography>
          <Typography variant="h6">Power Supply</Typography>
        </Grid>
          <Grid item xs={12} className={classes.button} align="center">
            <LinkContainer to="/step3">
              <Box pt={1}>
                <Button size="large" variant="containedSecondary" color="primary">DONE</Button>
              </Box>
            </LinkContainer>
            <LinkContainer to="/step1">
              <Box p={1}>
                <Button size="small" color="primary">Previous step</Button>
              </Box>
            </LinkContainer>
        </Grid>
      </Grid>
  );
}
