import React, {Component} from "react";
import "../containers/Home.css";
import {Button, Checkbox, FormGroup, FormControlLabel, Typography, Grid, Box, LinearProgress} from '@material-ui/core';
import { LinkContainer } from "react-router-bootstrap"
import {MuiThemeProvider, createMuiTheme} from '@material-ui/core'
import {red} from '@material-ui/core/colors'

const Step2 = () => {
  class FormHandler extends Component{
    constructor(props) {
        this.handleCheck = this.handleCheck.bind(this);
        super(props);
        this.state = {
        data: [apple, kiwi, banana, lime, orange, grape],
        checkedValues: []
        }
    }
    handleCheck(e,x) {
        this.setState(state => ({
        checkedValues: state.checkedValues.includes(x)
            ? state.checkedValues.filter(c => c !== x)
            : [...state.checkedValues, x]
        }));
    }
  }

  return (
    <Grid container direction="column" alignItems="center" justify= "center">
      <Grid item xs={12}>
        <Box width={300} align="center">
          <Typography variant="overline">Progress</Typography>
          <LinearProgress variant="determinate" value={100}/>
        </Box>
      </Grid>
        <Grid item xs={12}>
          <Checkbox
                    disabled={true}
                    onChange = {this.handleChange}
                    color="primary"
                    checked={this.state.checkboxstate.get(g.gid)}
          />
        </Grid>
      <Grid align="center" item xs={12}>
        <div>
          <LinkContainer to="/step2">
            <Box pt={1}>
              <Button size="large" variant="containedSecondary" color="primary">Next</Button>
              </Box>
          </LinkContainer>
        </div>
        <div>
          <LinkContainer to="/step1">
            <Box p={1}>
              <Button size="small" color="primary">Previous step</Button>
            </Box>
          </LinkContainer>
        </div>
      </Grid>
    </Grid>
  );
}
export default Step2;
