import React from "react";
import validateLogin from "./validateLogin";
import useFormValidation from "./useFormValidation";
import TopMenuBar from "../components/TopMenuBar";
import firebase from "../firebase";
import Firebase from 'firebase'
import {
  Avatar,
  Button,
  TextField,
  FormControlLabel,
  Checkbox,
  Typography,
  Container,
  makeStyles,
  Box,
  Grid,
  Link,
  CssBaseline
} from '@material-ui/core';

const useStyles = makeStyles(theme => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

const INITIAL_STATE ={
    name: "",
    email: "",
    password: ""
}

function Login(props) {
    const classes = useStyles();
    const {handleSubmit, handleBlur, handleChange, values, errors, isSubmitting} = useFormValidation(
        INITIAL_STATE,
        validateLogin,
        authenticateUser
    );


    Firebase.auth().onAuthStateChanged(function(user) {
        if (user) {
            console.log("User is signed in");
            props.history.push("/")
        } else {
            console.log("User is not signed in");

        }
    });


    const[login, setLogin] = React.useState(true);
    const [firebaseError, setFirebaseError] = React.useState(null)

    async function authenticateUser() {
        const {name, email, password} = values

        try{
            login
                ? await firebase.login(email, password)
                : await firebase.register(name, email, password)
            props.history.push("/")
        } catch (err) {
           console.error("Authentication error", err)
            setFirebaseError(err.message)
        }


    }
    return (

        <Container component="main" maxWidth="xs">
          <CssBaseline />
            <TopMenuBar block pageName="Quantified Dashboard" hamburgerMenu={false} closeButtonOnly={false} closeWithPrompt={false} backButton={false} backRoutePage="/"/>
            <Typography component="h1" variant="h5">{Login ? "Login" : "Create Account"}</Typography>
            <form  onSubmit={handleSubmit} className={classes.form}>
                {!login && (<input
                    onChange={handleChange}
                    value={values.name}
                    name="name"
                    type="text"
                    placeholder="Your name"
                    autoComplete="on"
                />)}
                <TextField
                    onChange={handleChange}
                    onBlur={handleBlur}
                    name="email"
                    variant="outlined"
                    label="Email Address"
                    id="email"
                    fullWidth
                    margin="normal"
                    required
                    value={values.email}
                    type="email"
                    className={errors.email && 'error-input'}
                    autoComplete="email"
                    autoFocus
                />
                {errors.email && <p className="error-text">{errors.email}</p>}
                <TextField
                    onChange={handleChange}
                    name="password"
                    variant="outlined"
                    id="password"
                    margin="normal"
                    label="Password"
                    fullWidth
                    required
                    type="password"
                    onBlur={handleBlur}
                    value={values.password}
                    className={errors.password && 'error-input'}
                    autoComplete="current-password"

                />
                {errors.password && <p className="error-text">{errors.password}</p>}
                {firebaseError && <p className="error-text">{firebaseError}</p> }
                <FormControlLabel
                  control={<Checkbox value="remember" color="primary"/>}
                  label="Remember me"
                  />
                <div className="flex mt3">
                    <Button
                      type="submit"
                      fullwidth
                      variant="contained"
                      color="primary"
                      className="button pointer mr2"
                      disabled={isSubmitting}
                      >
                        Submit
                    </Button>
                    <Button type="button" className="pointer button"
                            onClick={() => setLogin(prevLogin => !prevLogin)}>
                        {login ? "Register" : "Already have an account?"}
                    </Button>
                </div>
            </form>
        </Container>
    );
}

export default Login;
